   134→    credentials: 'include',  // Nutze Session Cookies für Auth
   135→    body: data ? JSON.stringify(data) : undefined
   136→  });
   137→  if (!response.ok) throw new Error(await response.text());
   138→  // DELETE returns often empty body or simple status
   139→  if (method === 'DELETE') return true;
   140→  return response.json();
   141→}
