   360→## src/hooks/useDashboardData.ts
   361→```
   362→import { useState, useEffect, useMemo, useCallback } from 'react';
   363→import type { Zutatendatenbank, Analysen, Analyseergebnisse, Schnellanalyse } from '@/types/app';
   364→import { LivingAppsService } from '@/services/livingAppsService';
   365→
   366→export function useDashboardData() {
   367→  const [zutatendatenbank, setZutatendatenbank] = useState<Zutatendatenbank[]>([]);
   368→  const [analysen, setAnalysen] = useState<Analysen[]>([]);
   369→  const [analyseergebnisse, setAnalyseergebnisse] = useState<Analyseergebnisse[]>([]);
   370→  const [schnellanalyse, setSchnellanalyse] = useState<Schnellanalyse[]>([]);
   371→  const [loading, setLoading] = useState(true);
   372→  const [error, setError] = useState<Error | null>(null);
   373→
   374→  const fetchAll = useCallback(async () => {
   375→    setError(null);
   376→    try {
   377→      const [zutatendatenbankData, analysenData, analyseergebnisseData, schnellanalyseData] = await Promise.all([
   378→        LivingAppsService.getZutatendatenbank(),
   379→        LivingAppsService.getAnalysen(),
   380→        LivingAppsService.getAnalyseergebnisse(),
   381→        LivingAppsService.getSchnellanalyse(),
   382→      ]);
   383→      setZutatendatenbank(zutatendatenbankData);
   384→      setAnalysen(analysenData);
   385→      setAnalyseergebnisse(analyseergebnisseData);
   386→      setSchnellanalyse(schnellanalyseData);
   387→    } catch (err) {
   388→      setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
   389→    } finally {
   390→      setLoading(false);
   391→    }
   392→  }, []);
   393→
   394→  useEffect(() => { fetchAll(); }, [fetchAll]);
   395→
   396→  const zutatendatenbankMap = useMemo(() => {
   397→    const m = new Map<string, Zutatendatenbank>();
   398→    zutatendatenbank.forEach(r => m.set(r.record_id, r));
   399→    return m;
   400→  }, [zutatendatenbank]);
   401→
   402→  const analysenMap = useMemo(() => {
   403→    const m = new Map<string, Analysen>();
   404→    analysen.forEach(r => m.set(r.record_id, r));
   405→    return m;
   406→  }, [analysen]);
   407→
   408→  return { zutatendatenbank, setZutatendatenbank, analysen, setAnalysen, analyseergebnisse, setAnalyseergebnisse, schnellanalyse, setSchnellanalyse, loading, error, fetchAll, zutatendatenbankMap, analysenMap };
   409→}
   410→```
