   427→## src/lib/enrich.ts
   428→```
   429→import type { EnrichedAnalyseergebnisse, EnrichedSchnellanalyse } from '@/types/enriched';
   430→import type { Analyseergebnisse, Analysen, Schnellanalyse, Zutatendatenbank } from '@/types/app';
   431→import { extractRecordId } from '@/services/livingAppsService';
   432→
   433→// eslint-disable-next-line @typescript-eslint/no-explicit-any
   434→function resolveDisplay(url: string | undefined, map: Map<string, any>, ...fields: string[]): string {
   435→  if (!url) return '';
   436→  const id = extractRecordId(url);
   437→  if (!id) return '';
   438→  const r = map.get(id);
   439→  if (!r) return '';
   440→  return fields.map(f => String(r.fields[f] ?? '')).join(' ').trim();
   441→}
   442→
   443→interface AnalyseergebnisseMaps {
   444→  analysenMap: Map<string, Analysen>;
   445→  zutatendatenbankMap: Map<string, Zutatendatenbank>;
   446→}
   447→
   448→export function enrichAnalyseergebnisse(
   449→  analyseergebnisse: Analyseergebnisse[],
   450→  maps: AnalyseergebnisseMaps
   451→): EnrichedAnalyseergebnisse[] {
   452→  return analyseergebnisse.map(r => ({
   453→    ...r,
   454→    zugehoerende_analyseName: resolveDisplay(r.fields.zugehoerende_analyse, maps.analysenMap, 'analyse_titel'),
   455→    erkannte_zutatName: resolveDisplay(r.fields.erkannte_zutat, maps.zutatendatenbankMap, 'zutat_name'),
   456→  }));
   457→}
   458→
   459→interface SchnellanalyseMaps {
   460→  zutatendatenbankMap: Map<string, Zutatendatenbank>;
   461→}
   462→
   463→export function enrichSchnellanalyse(
   464→  schnellanalyse: Schnellanalyse[],
   465→  maps: SchnellanalyseMaps
   466→): EnrichedSchnellanalyse[] {
   467→  return schnellanalyse.map(r => ({
   468→    ...r,
   469→    erkannte_zutatenName: resolveDisplay(r.fields.erkannte_zutaten, maps.zutatendatenbankMap, 'zutat_name'),
   470→  }));
   471→}
   472→
