export default function DashboardOverview() {
  const {
    zutatendatenbank, analysen, analyseergebnisse, schnellanalyse,
    zutatendatenbankMap, analysenMap,
    loading, error, fetchAll,
  } = useDashboardData();

  const enrichedAnalyseergebnisse = enrichAnalyseergebnisse(analyseergebnisse, { analysenMap, zutatendatenbankMap });
  const enrichedSchnellanalyse = enrichSchnellanalyse(schnellanalyse, { zutatendatenbankMap });

  if (loading) return <DashboardSkeleton />;
  if (error) return <DashboardError error={error} onRetry={fetchAll} />;

  return (
    <div className="space-y-8">
    </div>
  );
}

function DashboardSkeleton() {
  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <Skeleton className="h-8 w-48" />
