   464→            setDeleteAnalyse(null);
   465→            fetchAll();
   466→          }
   467→        }}
   468→        onClose={() => setDeleteAnalyse(null)}
   469→      />
   470→
   471→      {/* Analyseergebnis create/edit */}
   472→      <AnalyseergebnisseDialog
   473→        open={ergebnisDialogOpen}
   474→        onClose={() => { setErgebnisDialogOpen(false); setEditErgebnis(null); setNewErgebnisAnalyseId(null); }}
   475→        onSubmit={async (fields) => {
   476→          if (editErgebnis) {
   477→            await LivingAppsService.updateAnalyseergebnisseEntry(editErgebnis.record_id, fields);
   478→          } else {
   479→            await LivingAppsService.createAnalyseergebnisseEntry(fields);
   480→          }
   481→          fetchAll();
   482→        }}
   483→        defaultValues={
   484→          editErgebnis
   485→            ? editErgebnis.fields
   486→            : newErgebnisAnalyseId
   487→              ? { zugehoerende_analyse: createRecordUrl(APP_IDS.ANALYSEN, newErgebnisAnalyseId) }
   488→              : undefined
   489→        }
   490→        analysenList={analysen}
   491→        zutatendatenbankList={zutatendatenbank}
   492→        enablePhotoScan={AI_PHOTO_SCAN['Analyseergebnisse']}
   493→      />
   494→
   495→      {/* Analyseergebnis delete */}
   496→      <ConfirmDialog
   497→        open={!!deleteErgebnis}
   498→        title="Ergebnis löschen"
   499→        description="Diesen Eintrag wirklich löschen?"
   500→        onConfirm={async () => {
   501→          if (deleteErgebnis) {
   502→            await LivingAppsService.deleteAnalyseergebnisseEntry(deleteErgebnis.record_id);
   503→            setDeleteErgebnis(null);
   504→            fetchAll();
   505→          }
   506→        }}
   507→        onClose={() => setDeleteErgebnis(null)}
   508→      />
   509→
   510→      {/* Schnellanalyse */}
   511→      <SchnellanalyseDialog
   512→        open={schnellDialogOpen}
   513→        onClose={() => setSchnellDialogOpen(false)}
   514→        onSubmit={async (fields) => {
   515→          await LivingAppsService.createSchnellanalyseEntry(fields);
   516→          fetchAll();
   517→        }}
   518→        zutatendatenbankList={zutatendatenbank}
   519→        enablePhotoScan={AI_PHOTO_SCAN['Schnellanalyse']}
   520→      />
   521→    </div>
   522→  );
   523→}
   524→
   525→function DashboardSkeleton() {
   526→  return (
   527→    <div className="space-y-6">
