   530→        <Skeleton className="h-9 w-36" />
   531→      </div>
   532→      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3">
   533→        {[...Array(4)].map((_, i) => <Skeleton key={i} className="h-24 rounded-2xl" />)}
   534→      </div>
   535→      <Skeleton className="h-64 rounded-2xl" />
   536→    </div>
   537→  );
   538→}
   539→
   540→function DashboardError({ error, onRetry }: { error: Error; onRetry: () => void }) {
   541→  return (
   542→    <div className="flex flex-col items-center justify-center py-24 gap-4">
   543→      <div className="w-12 h-12 rounded-2xl bg-destructive/10 flex items-center justify-center">
   544→        <AlertCircle size={22} className="text-destructive" />
   545→      </div>
   546→      <div className="text-center">
   547→        <h3 className="font-semibold text-foreground mb-1">Fehler beim Laden</h3>
